import React from "react"
import ReactDOM from "react-dom"

const Header = (props) => {
  return (
    <div>
      <h1>{props.course}</h1>
    </div>
  )
}

const Part = (props) => {
  console.log(props)
  return (
    <div>
      <p>{props.part.name} {props.part.exercises}</p>
    </div>
  )
}

const Content = (props) => {
  return (
    <div>
      <Part part={props.partInfo[0]}/>
      <Part part={props.partInfo[1]}/>
      <Part part={props.partInfo[2]}/>
    </div>
  )
}

const Total = (props) => {
  return (
    <div>
      <p>Number of exercises {props.partInfo.p1[1] + props.partInfo.p2[1] + props.partInfo.p3[1]}</p>
    </div>
  )
}

const App = () => {
  const course = "Half Stack application development"

  const part1 = {
    name: "Fundamentals of React",
    exercises: 10
  }

  const part2 = {
    name: "Using props to pass data",
    exercises: 7
  }

  const part3 = {
    name: "State of a component",
    exercises: 14
  }

  return (
    <div>
      <Header course={course}/>
      <Content partInfo={[part1, part2, part3]}/>
      {/* <Total partInfo={partInfo}/> */}
    </div>
  )
}

ReactDOM.render(<App />, document.getElementById("root"))
